		address indexed _to,
		uint256 indexed _destinationChainId,
		address _from,
		uint256 _originChainId,
		uint256 _amount,
		bytes _userData
	);

	event NewSideToken(
		address indexed _newSideTokenAddress,
		address indexed _originalTokenAddress,
		string _newSymbol,
		uint256 _granularity,
		uint256 _chainId
	);
	event AcceptedCrossTransfer(
